.btn {
  padding: 0.5rem 1.5rem;
  border: none;
  border-radius: 4px;
  font-size: 1rem;
  cursor: pointer;
  transition: all 0.3s;
  font-weight: 500;

  &:disabled {
    opacity: 0.6;
    cursor: not-allowed;
  }

  &.btn-primary {
    background-color: #3498db;
    color: white;

    &:hover:not(:disabled) {
      background-color: #2980b9;
    }
  }

  &.btn-secondary {
    background-color: #95a5a6;
    color: white;

    &:hover:not(:disabled) {
      background-color: #7f8c8d;
    }
  }

  &.btn-danger {
    background-color: #e74c3c;
    color: white;

    &:hover:not(:disabled) {
      background-color: #c0392b;
    }
  }
}

