import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-header',
  templateUrl: './app-header.component.html',
  styleUrls: ['./app-header.component.scss']
})
export class AppHeaderComponent implements OnInit {
  title = 'MotherSon Demo';

  masterComponents = [
    { name: 'Customer Master', route: '/master/customer-master' },
    { name: 'Product Master', route: '/master/product-master' },
    { name: 'BMR/BPR Tracking', route: '/master/bmr-bpr-numbering-and-tracking' },
    { name: 'Checklist', route: '/master/checklist-for-checking-counterfeit-spurious-medicine' },
    { name: 'Sample Program', route: '/master/sample-program' }
  ];

  recordComponents = [
    { name: 'Cleaning Record', route: '/record/cleaning-record' },
    { name: 'DO Activity', route: '/record/do-activity' },
    { name: 'Equipment Usage', route: '/record/equipment-usage-record' },
    { name: 'Line Clearance', route: '/record/line-clearance-record' }
  ];

  logComponents = [
    { name: 'Authorised List', route: '/logs/authorised-list-log' },
    { name: 'Autonomous Maintenance', route: '/logs/autonomous-maintenance-standard-clti' },
    { name: 'Batch Cancellation', route: '/logs/batch-record-cancellation-request-log' },
    { name: 'Certificate Numbering', route: '/logs/certificate-numbering-log' },
    { name: 'Checklist Management', route: '/logs/checklist-management-log' },
    { name: 'Datasheet Selection', route: '/logs/datasheet-for-selection-of-fill-cam' },
    { name: 'Disinfectant Preparation', route: '/logs/disinfectant-solution-preparation-log' },
    { name: 'DO HOS Verification', route: '/logs/do-hos-verification-calibration-and-usage-record-log' }
  ];

  showMasterDropdown = false;
  showRecordDropdown = false;
  showLogsDropdown = false;

  constructor() { }

  ngOnInit(): void {
  }

  toggleMasterDropdown(): void {
    this.showMasterDropdown = !this.showMasterDropdown;
    this.showRecordDropdown = false;
    this.showLogsDropdown = false;
  }

  toggleRecordDropdown(): void {
    this.showRecordDropdown = !this.showRecordDropdown;
    this.showMasterDropdown = false;
    this.showLogsDropdown = false;
  }

  toggleLogsDropdown(): void {
    this.showLogsDropdown = !this.showLogsDropdown;
    this.showMasterDropdown = false;
    this.showRecordDropdown = false;
  }
}
