import { Component, OnInit } from '@angular/core';

interface Employee {
  empId: string;
  salary: number;
}

@Component({
  selector: 'app-employee-data',
  templateUrl: './employee-data.component.html',
  styleUrls: ['./employee-data.component.scss']
})
export class EmployeeDataComponent implements OnInit {

  employees: Employee[] = [];
  newEmployee: Employee = {
    empId: '',
    salary: 0
  };

  constructor() { }

  ngOnInit(): void {
    // Initialize with mock data
    this.employees = [
      { empId: 'EMP001', salary: 75000 },
      { empId: 'EMP002', salary: 82000 },
      { empId: 'EMP003', salary: 68000 },
      { empId: 'EMP004', salary: 95000 },
      { empId: 'EMP005', salary: 71000 }
    ];
  }

  addEmployee(): void {
    if (this.newEmployee.empId.trim() && this.newEmployee.salary > 0) {
      // Check if employee ID already exists
      const existingEmployee = this.employees.find(emp => emp.empId === this.newEmployee.empId);

      if (existingEmployee) {
        // Update existing employee salary
        existingEmployee.salary = this.newEmployee.salary;
      } else {
        // Add new employee
        this.employees.push({
          empId: this.newEmployee.empId,
          salary: this.newEmployee.salary
        });
      }

      // Reset form
      this.newEmployee = {
        empId: '',
        salary: 0
      };
    }
  }

}
