import { ChangeDetectionStrategy, Component, OnInit } from '@angular/core';
import { BehaviorSubject } from 'rxjs';

interface Employee {
  empId: string;
  salary: number;
}

interface TableColumn {
  field: string;
  header: string;
  type: string;
}

interface TableAction {
  label: string;
  action: string;
  type: string;
}

interface ActionEvent {
  action: string;
  data: Employee;
}

@Component({
  selector: 'app-employee-salary',
  templateUrl: './employee-salary.component.html',
  styleUrls: ['./employee-salary.component.scss'],
  changeDetection: ChangeDetectionStrategy.OnPush
})
export class EmployeeSalaryComponent implements OnInit {
  newEmpId: string = '';
  newSalary: number = 0;

  private _employees$ = new BehaviorSubject<Employee[]>([
    { empId: 'EMP001', salary: 50000 },
    { empId: 'EMP002', salary: 75000 },
    { empId: 'EMP003', salary: 60000 },
    { empId: 'EMP004', salary: 85000 },
    { empId: 'EMP005', salary: 45000 }
  ]);
  employeeData$ = this._employees$.asObservable();

  tableColumns: TableColumn[] = [
    { field: 'empId', header: 'EMP ID', type: 'text' },
    { field: 'salary', header: 'Salary', type: 'currency' }
  ];

  tableActions: TableAction[] = [
    { label: 'Delete', action: 'delete', type: 'danger' }
  ];

  constructor() { }

  ngOnInit(): void {
    // Component initialization logic
  }

  addEmployee(): void {
    if (this.newEmpId && this.newSalary > 0) {
      const newEmployee: Employee = {
        empId: this.newEmpId.trim(),
        salary: this.newSalary
      };

      const currentEmployees = this._employees$.value;
      this._employees$.next([...currentEmployees, newEmployee]);

      // Reset form
      this.newEmpId = '';
      this.newSalary = 0;
    }
  }

  handleAction(event: ActionEvent): void {
    if (event.action === 'delete') {
      const currentEmployees = this._employees$.value;
      this._employees$.next(currentEmployees.filter(emp => emp.empId !== event.data.empId));
    }
  }
}
