import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-button-click-page',
  templateUrl: './button-click-page.component.html',
  styleUrls: ['./button-click-page.component.scss']
})
export class ButtonClickPageComponent implements OnInit {

  showMessage: boolean = false;
  clickCount: number = 0;

  constructor() { }

  ngOnInit(): void {
    // Component initialization logic
  }

  onButtonClick(): void {
    this.clickCount++;
    this.showMessage = true;

    // Optional: Hide message after 3 seconds
    setTimeout(() => {
      this.showMessage = false;
    }, 3000);
  }

}
