import { Component, OnInit } from '@angular/core';

interface Employee {
  empId: string;
  salary: number;
}

interface TableColumn {
  field: string;
  header: string;
  type: string;
}

@Component({
  selector: 'app-employee-salary',
  templateUrl: './employee-salary.component.html',
  styleUrls: ['./employee-salary.component.scss']
})
export class EmployeeSalaryComponent implements OnInit {
  employees: Employee[] = [];
  newEmployee: Employee = { empId: '', salary: 0 };
  isLoading: boolean = false;
  isTableLoading: boolean = false;

  tableColumns: TableColumn[] = [
    { field: 'empId', header: 'EMP ID', type: 'text' },
    { field: 'salary', header: 'Salary', type: 'currency' }
  ];

  constructor() { }

  ngOnInit(): void {
    this.loadInitialData();
  }

  loadInitialData(): void {
    this.isTableLoading = true;

    setTimeout(() => {
      this.employees = [
        { empId: 'EMP001', salary: 50000 },
        { empId: 'EMP002', salary: 65000 },
        { empId: 'EMP003', salary: 45000 },
        { empId: 'EMP004', salary: 72000 },
        { empId: 'EMP005', salary: 58000 }
      ];
      this.isTableLoading = false;
    }, 500);
  }

  addEmployee(): void {
    if (this.newEmployee.empId && this.newEmployee.salary > 0) {
      this.isLoading = true;

      setTimeout(() => {
        this.employees = [...this.employees, { ...this.newEmployee }];
        this.clearForm();
        this.isLoading = false;
      }, 300);
    }
  }

  clearForm(): void {
    this.newEmployee = { empId: '', salary: 0 };
  }
}
