import { Component, OnInit } from '@angular/core';

interface Employee {
  empId: string;
  salary: number;
}

@Component({
  selector: 'app-employee-salary',
  templateUrl: './employee-salary.component.html',
  styleUrls: ['./employee-salary.component.scss']
})
export class EmployeeSalaryComponent implements OnInit {
  employees: Employee[] = [];
  newEmpId: string = '';
  newSalary: number = 0;

  constructor() { }

  ngOnInit(): void {
    // Initialize with mock data
    this.employees = [
      { empId: 'EMP001', salary: 75000 },
      { empId: 'EMP002', salary: 85000 },
      { empId: 'EMP003', salary: 65000 },
      { empId: 'EMP004', salary: 95000 },
      { empId: 'EMP005', salary: 70000 }
    ];
  }

  addEmployee(): void {
    if (this.newEmpId.trim() && this.newSalary > 0) {
      const newEmployee: Employee = {
        empId: this.newEmpId.trim(),
        salary: this.newSalary
      };

      this.employees.push(newEmployee);

      // Reset form fields
      this.newEmpId = '';
      this.newSalary = 0;
    }
  }
}
