import { Component, OnInit } from '@angular/core';

interface Employee {
  empId: string;
  salary: number;
}

interface TableColumn {
  field: string;
  header: string;
  type: string;
}

interface TableAction {
  label: string;
  action: string;
  class: string;
}

@Component({
  selector: 'app-employee-salary',
  templateUrl: './employee-salary.component.html',
  styleUrls: ['./employee-salary.component.scss']
})
export class EmployeeSalaryComponent implements OnInit {
  newEmpId: string = '';
  newSalary: number = 0;

  employeeData: Employee[] = [];

  tableColumns: TableColumn[] = [
    { field: 'empId', header: 'EMP ID', type: 'text' },
    { field: 'salary', header: 'Salary', type: 'currency' }
  ];

  tableActions: TableAction[] = [
    { label: 'Delete', action: 'delete', class: 'danger' }
  ];

  constructor() { }

  ngOnInit(): void {
    // Initialize with mock data
    this.employeeData = [
      { empId: 'EMP001', salary: 50000 },
      { empId: 'EMP002', salary: 75000 },
      { empId: 'EMP003', salary: 60000 },
      { empId: 'EMP004', salary: 85000 },
      { empId: 'EMP005', salary: 45000 }
    ];
  }

  addEmployee(): void {
    if (this.newEmpId.trim() && this.newSalary > 0) {
      const newEmployee: Employee = {
        empId: this.newEmpId.trim(),
        salary: this.newSalary
      };

      this.employeeData = [...this.employeeData, newEmployee];

      // Reset form
      this.newEmpId = '';
      this.newSalary = 0;
    }
  }

  onTableAction(event: any): void {
    if (event.action === 'delete') {
      this.employeeData = this.employeeData.filter(emp => emp.empId !== event.row.empId);
    }
  }
}
