import { Component, OnInit, ChangeDetectionStrategy } from '@angular/core';
import { BehaviorSubject, Observable } from 'rxjs';
import { debounceTime, distinctUntilChanged, map, startWith } from 'rxjs/operators';

type Status = 'Active' | 'Inactive' | 'Pending';

interface Employee {
  id: number;
  name: string;
  email: string;
  department: string;
  position: string;
  status: Status;
  statusClass?: string;
}

@Component({
  selector: 'app-table-search-page',
  templateUrl: './table-search-page.component.html',
  styleUrls: ['./table-search-page.component.scss'],
  changeDetection: ChangeDetectionStrategy.OnPush
})
export class TableSearchPageComponent implements OnInit {
  private employees: Employee[] = [];
  private search$ = new BehaviorSubject<string>('');
  filteredEmployees$: Observable<Employee[]>;

  constructor() { }

  ngOnInit(): void {
    this.employees = [
      {
        id: 1,
        name: 'John Smith',
        email: 'john.smith@company.com',
        department: 'Engineering',
        position: 'Senior Developer',
        status: 'Active',
        statusClass: 'active'
      },
      {
        id: 2,
        name: 'Sarah Johnson',
        email: 'sarah.johnson@company.com',
        department: 'Marketing',
        position: 'Marketing Manager',
        status: 'Active',
        statusClass: 'active'
      },
      {
        id: 3,
        name: 'Michael Chen',
        email: 'michael.chen@company.com',
        department: 'Engineering',
        position: 'DevOps Engineer',
        status: 'Pending',
        statusClass: 'pending'
      },
      {
        id: 4,
        name: 'Emily Davis',
        email: 'emily.davis@company.com',
        department: 'Human Resources',
        position: 'HR Specialist',
        status: 'Active',
        statusClass: 'active'
      },
      {
        id: 5,
        name: 'Robert Wilson',
        email: 'robert.wilson@company.com',
        department: 'Sales',
        position: 'Sales Representative',
        status: 'Inactive',
        statusClass: 'inactive'
      },
      {
        id: 6,
        name: 'Jennifer Brown',
        email: 'jennifer.brown@company.com',
        department: 'Finance',
        position: 'Financial Analyst',
        status: 'Active',
        statusClass: 'active'
      }
    ];

    this.filteredEmployees$ = this.search$
      .pipe(
        debounceTime(300),
        distinctUntilChanged(),
        startWith(''),
        map(term => {
          if (!term) { return this.employees; }
          const t = term.toLowerCase();
          return this.employees.filter(e =>
            e.name.toLowerCase().includes(t) ||
            e.email.toLowerCase().includes(t) ||
            e.department.toLowerCase().includes(t) ||
            e.position.toLowerCase().includes(t)
          );
        })
      );
  }

  onSearch(term: string): void {
    this.search$.next(term);
  }
}
