import { Component, OnInit } from '@angular/core';

interface Product {
  id: number;
  name: string;
  category: string;
  price: number;
  stock: number;
  status: 'active' | 'inactive';
  createdDate: Date;
}

interface TableColumn {
  key: string;
  header: string;
  type: 'text' | 'number' | 'currency' | 'date' | 'status' | 'boolean';
  sortable?: boolean;
}

@Component({
  selector: 'app-search-table-page',
  templateUrl: './search-table-page.component.html',
  styleUrls: ['./search-table-page.component.scss']
})
export class SearchTablePageComponent implements OnInit {
  searchTerm: string = '';
  isLoading: boolean = false;
  allProducts: Product[] = [];
  displayedData: Product[] = [];

  tableColumns: TableColumn[] = [
    { key: 'id', header: 'ID', type: 'number', sortable: true },
    { key: 'name', header: 'Product Name', type: 'text', sortable: true },
    { key: 'category', header: 'Category', type: 'text', sortable: true },
    { key: 'price', header: 'Price', type: 'currency', sortable: true },
    { key: 'stock', header: 'Stock', type: 'number', sortable: true },
    { key: 'status', header: 'Status', type: 'status', sortable: true },
    { key: 'createdDate', header: 'Created Date', type: 'date', sortable: true }
  ];

  constructor() { }

  ngOnInit(): void {
    this.initializeMockData();
    this.displayedData = [...this.allProducts];
  }

  initializeMockData(): void {
    this.allProducts = [
      {
        id: 1,
        name: 'Wireless Mouse',
        category: 'Electronics',
        price: 29.99,
        stock: 150,
        status: 'active',
        createdDate: new Date('2024-01-15')
      },
      {
        id: 2,
        name: 'Office Chair',
        category: 'Furniture',
        price: 199.99,
        stock: 45,
        status: 'active',
        createdDate: new Date('2024-01-20')
      },
      {
        id: 3,
        name: 'Coffee Maker',
        category: 'Appliances',
        price: 89.99,
        stock: 0,
        status: 'inactive',
        createdDate: new Date('2024-02-01')
      },
      {
        id: 4,
        name: 'Smartphone',
        category: 'Electronics',
        price: 699.99,
        stock: 75,
        status: 'active',
        createdDate: new Date('2024-02-10')
      },
      {
        id: 5,
        name: 'Desk Lamp',
        category: 'Furniture',
        price: 49.99,
        stock: 120,
        status: 'active',
        createdDate: new Date('2024-02-15')
      },
      {
        id: 6,
        name: 'Bluetooth Speaker',
        category: 'Electronics',
        price: 79.99,
        stock: 200,
        status: 'active',
        createdDate: new Date('2024-03-01')
      }
    ];
  }

  onSearchChange(searchTerm: string): void {
    this.searchTerm = searchTerm;
  }

  performSearch(): void {
    this.isLoading = true;

    setTimeout(() => {
      if (!this.searchTerm.trim()) {
        this.displayedData = [...this.allProducts];
      } else {
        const searchLower = this.searchTerm.toLowerCase();
        this.displayedData = this.allProducts.filter(product => 
          product.name.toLowerCase().includes(searchLower) ||
          product.category.toLowerCase().includes(searchLower)
        );
      }
      this.isLoading = false;
    }, 500);
  }

  onTableAction(event: any): void {
    console.log('Table action triggered:', event);
  }
}
