import { Component, OnInit } from '@angular/core';

interface Employee {
  empId: string;
  salary: number;
}

@Component({
  selector: 'app-employee-data-table',
  templateUrl: './employee-data-table.component.html',
  styleUrls: ['./employee-data-table.component.scss']
})
export class EmployeeDataTableComponent implements OnInit {

  employees: Employee[] = [];
  filteredEmployees: Employee[] = [];
  newEmployee: Employee = {
    empId: '',
    salary: 0
  };
  searchTerm: string = '';

  constructor() { }

  ngOnInit(): void {
    this.initializeMockData();
    this.filteredEmployees = [...this.employees];
  }

  private initializeMockData(): void {
    this.employees = [
      { empId: 'EMP001', salary: 75000 },
      { empId: 'EMP002', salary: 82000 },
      { empId: 'EMP003', salary: 68000 },
      { empId: 'EMP004', salary: 95000 },
      { empId: 'EMP005', salary: 71000 }
    ];
  }

  addEmployee(): void {
    if (this.newEmployee.empId.trim() && this.newEmployee.salary > 0) {
      // Check if employee ID already exists
      const existingEmployee = this.employees.find(emp => emp.empId === this.newEmployee.empId.trim());

      if (existingEmployee) {
        alert('Employee ID already exists!');
        return;
      }

      // Add new employee
      const employeeToAdd: Employee = {
        empId: this.newEmployee.empId.trim(),
        salary: this.newEmployee.salary
      };

      this.employees.push(employeeToAdd);
      this.applyFilter();

      // Reset form
      this.newEmployee = {
        empId: '',
        salary: 0
      };
    } else {
      alert('Please fill in both EMP ID and Salary fields with valid values.');
    }
  }

  onSearch(searchTerm: string): void {
    this.searchTerm = searchTerm;
    this.applyFilter();
  }

  private applyFilter(): void {
    if (!this.searchTerm.trim()) {
      this.filteredEmployees = [...this.employees];
    } else {
      const term = this.searchTerm.toLowerCase().trim();
      this.filteredEmployees = this.employees.filter(employee => 
        employee.empId.toLowerCase().includes(term) ||
        employee.salary.toString().includes(term)
      );
    }
  }

  trackByEmpId(index: number, employee: Employee): string {
    return employee.empId;
  }

}
