import { Component, OnInit } from '@angular/core';

interface Employee {
  empId: string;
  salary: number;
}

@Component({
  selector: 'app-employee-data-table',
  templateUrl: './employee-data-table.component.html',
  styleUrls: ['./employee-data-table.component.scss']
})
export class EmployeeDataTableComponent implements OnInit {

  employees: Employee[] = [];
  filteredEmployees: Employee[] = [];
  newEmployee: Employee = {
    empId: '',
    salary: 0
  };

  sortField: string = '';
  sortDirection: 'asc' | 'desc' = 'asc';
  searchTerm: string = '';

  constructor() { }

  ngOnInit(): void {
    this.initializeMockData();
    this.filteredEmployees = [...this.employees];
  }

  private initializeMockData(): void {
    this.employees = [
      { empId: 'EMP001', salary: 75000 },
      { empId: 'EMP002', salary: 82000 },
      { empId: 'EMP003', salary: 68000 },
      { empId: 'EMP004', salary: 95000 },
      { empId: 'EMP005', salary: 71000 }
    ];
  }

  addEmployee(): void {
    if (this.isFormValid()) {
      const employeeExists = this.employees.some(emp => emp.empId === this.newEmployee.empId);

      if (employeeExists) {
        alert('Employee ID already exists!');
        return;
      }

      this.employees.push({
        empId: this.newEmployee.empId,
        salary: this.newEmployee.salary
      });

      this.applyFilter();
      this.resetForm();
    }
  }

  isFormValid(): boolean {
    return this.newEmployee.empId.trim() !== '' && 
           this.newEmployee.salary > 0;
  }

  private resetForm(): void {
    this.newEmployee = {
      empId: '',
      salary: 0
    };
  }

  onSearch(searchTerm: string): void {
    this.searchTerm = searchTerm;
    this.applyFilter();
  }

  private applyFilter(): void {
    if (!this.searchTerm) {
      this.filteredEmployees = [...this.employees];
    } else {
      const term = this.searchTerm.toLowerCase();
      this.filteredEmployees = this.employees.filter(employee => 
        employee.empId.toLowerCase().includes(term) ||
        employee.salary.toString().includes(term)
      );
    }

    if (this.sortField) {
      this.applySorting();
    }
  }

  sortBy(field: keyof Employee): void {
    if (this.sortField === field) {
      this.sortDirection = this.sortDirection === 'asc' ? 'desc' : 'asc';
    } else {
      this.sortField = field;
      this.sortDirection = 'asc';
    }

    this.applySorting();
  }

  private applySorting(): void {
    this.filteredEmployees.sort((a, b) => {
      const aValue = a[this.sortField as keyof Employee];
      const bValue = b[this.sortField as keyof Employee];

      let comparison = 0;
      if (aValue > bValue) {
        comparison = 1;
      } else if (aValue < bValue) {
        comparison = -1;
      }

      return this.sortDirection === 'desc' ? comparison * -1 : comparison;
    });
  }

  getSortIcon(field: string): string {
    if (this.sortField !== field) {
      return '↕';
    }
    return this.sortDirection === 'asc' ? '↑' : '↓';
  }

  trackByEmpId(index: number, employee: Employee): string {
    return employee.empId;
  }
}
