import { Component, OnInit } from '@angular/core';

interface DateRange {
  startDate: string | null;
  endDate: string | null;
}

@Component({
  selector: 'app-date-range-display',
  templateUrl: './date-range-display.component.html',
  styleUrls: ['./date-range-display.component.scss']
})
export class DateRangeDisplayComponent implements OnInit {

  selectedRange: DateRange = {
    startDate: null,
    endDate: null
  };

  displayedRange: DateRange = {
    startDate: null,
    endDate: null
  };

  constructor() { }

  ngOnInit(): void {
    // Component initialization logic
  }

  onDateRangeChange(range: DateRange): void {
    this.selectedRange = {
      startDate: range.startDate,
      endDate: range.endDate
    };
  }

  updateDisplayedRange(): void {
    this.displayedRange = {
      startDate: this.selectedRange.startDate,
      endDate: this.selectedRange.endDate
    };
  }

}
