import { Component, EventEmitter, Input, OnInit, Output, ElementRef, HostListener } from '@angular/core';

@Component({
    selector: 'app-autocomplete',
    templateUrl: './app-autocomplete.component.html',
    styleUrls: ['./app-autocomplete.component.scss']
})
export class AppAutocompleteComponent implements OnInit {

    @Input() data: any[] = [];
    @Input() labelKey: string = 'name';
    @Input() placeholder: string = 'Start typing...';

    @Output() selected = new EventEmitter<any>();

    query: string = '';
    filteredData: any[] = [];
    showSuggestions: boolean = false;

    constructor(private eRef: ElementRef) { }

    ngOnInit(): void {
    }

    onInput() {
        if (!this.query) {
            this.filteredData = [];
            this.showSuggestions = false;
            return;
        }

        const q = this.query.toLowerCase();
        this.filteredData = this.data.filter(item => {
            const val = this.getItemLabel(item).toLowerCase();
            return val.includes(q);
        });
        this.showSuggestions = this.filteredData.length > 0;
    }

    selectItem(item: any) {
        this.query = this.getItemLabel(item);
        this.selected.emit(item);
        this.showSuggestions = false;
    }

    getItemLabel(item: any): string {
        return typeof item === 'object' ? item[this.labelKey] : item;
    }

    @HostListener('document:click', ['$event'])
    clickout(event: any) {
        if (!this.eRef.nativeElement.contains(event.target)) {
            this.showSuggestions = false;
        }
    }
}

