import { Component, EventEmitter, Input, OnInit, Output } from '@angular/core';

@Component({
    selector: 'app-file-upload',
    templateUrl: './app-file-upload.component.html',
    styleUrls: ['./app-file-upload.component.scss']
})
export class AppFileUploadComponent implements OnInit {

    @Input() allowedTypes: string[] = ['image/png', 'image/jpeg', 'application/pdf'];
    @Input() maxSizeMB: number = 5;
    @Output() fileSelected = new EventEmitter<File>();

    selectedFileName: string | null = null;
    error: string | null = null;

    constructor() { }

    ngOnInit(): void {
    }

    onFileChange(event: any) {
        this.error = null;
        const file = event.target.files[0];

        if (file) {
            if (!this.allowedTypes.includes(file.type) && this.allowedTypes.length > 0) {
                this.error = 'File type not allowed. Allowed: ' + this.allowedTypes.join(', ');
                return;
            }

            const sizeMB = file.size / (1024 * 1024);
            if (sizeMB > this.maxSizeMB) {
                this.error = `File is too large (Max ${this.maxSizeMB}MB)`;
                return;
            }

            this.selectedFileName = file.name;
            this.fileSelected.emit(file);
        }
    }
}

