import { Component, Input, OnInit } from '@angular/core';

@Component({
    selector: 'app-status-badge',
    templateUrl: './app-status-badge.component.html',
    styleUrls: ['./app-status-badge.component.scss']
})
export class AppStatusBadgeComponent implements OnInit {

    @Input() status: string = '';
    @Input() mapping: { [key: string]: string } = {};

    constructor() { }

    ngOnInit(): void {
    }

    getBadgeClass(): string {
        const s = this.status.toLowerCase();

        // Check custom mapping first using lower-case key
        if (this.mapping && this.mapping[s]) {
            return `badge-${this.mapping[s]}`;
        }

        // Default heuristics
        if (['active', 'approved', 'completed', 'success', 'pass', 'open'].includes(s)) return 'badge-success';
        if (['inactive', 'rejected', 'failed', 'error', 'closed'].includes(s)) return 'badge-danger';
        if (['pending', 'in progress', 'waiting', 'warning'].includes(s)) return 'badge-warning';
        if (['draft', 'info', 'new'].includes(s)) return 'badge-info';

        return 'badge-secondary';
    }
}
