import { Component, EventEmitter, Input, OnInit, Output } from '@angular/core';

@Component({
    selector: 'app-action-buttons',
    templateUrl: './app-action-buttons.component.html',
    styleUrls: ['./app-action-buttons.component.scss']
})
export class AppActionButtonsComponent implements OnInit {

    @Input() config: string[] = []; // ['edit', 'delete', 'view', 'approve']
    @Output() clicked = new EventEmitter<string>();

    constructor() { }

    ngOnInit(): void {
    }

    onClick(action: string) {
        this.clicked.emit(action);
    }

    getIcon(action: string): string {
        switch (action.toLowerCase()) {
            case 'edit': return 'fa fa-pencil';
            case 'delete': return 'fa fa-trash';
            case 'view': return 'fa fa-eye';
            case 'info': return 'fa fa-info-circle';
            case 'check':
            case 'approve': return 'fa fa-check';
            case 'cancel':
            case 'reject': return 'fa fa-times';
            default: return 'fa fa-cog';
        }
    }

    getClass(action: string): string {
        switch (action.toLowerCase()) {
            case 'edit': return 'btn-outline-primary';
            case 'delete': return 'btn-outline-danger';
            case 'view': return 'btn-outline-info';
            case 'check':
            case 'approve': return 'btn-outline-success';
            default: return 'btn-outline-secondary';
        }
    }

    getTitle(action: string): string {
        return action.charAt(0).toUpperCase() + action.slice(1);
    }
}

