import { Component, EventEmitter, Input, OnInit, Output } from '@angular/core';

@Component({
    selector: 'app-data-table',
    templateUrl: './app-data-table.component.html',
    styleUrls: ['./app-data-table.component.scss']
})
export class AppDataTableComponent implements OnInit {

    @Input() data: any[] = [];
    @Input() cols: { field: string, header: string, type?: string }[] = [];
    @Input() actions: string[] = []; // e.g. ['edit', 'delete']
    @Input() loading: boolean = false;

    @Output() actionClicked = new EventEmitter<{ action: string, row: any }>();

    constructor() { }

    ngOnInit(): void {
    }

    onAction(action: string, row: any) {
        this.actionClicked.emit({ action, row });
    }

    getCellValue(row: any, field: string): any {
        // Handle nested properties if needed (e.g. 'user.name')
        return field.split('.').reduce((acc, part) => acc && acc[part], row);
    }
}

import { Component, EventEmitter, Input, OnInit, Output } from '@angular/core';

@Component({
    selector: 'app-date-range',
    templateUrl: './app-date-range.component.html',
    styleUrls: ['./app-date-range.component.scss']
})
export class AppDateRangeComponent implements OnInit {

    @Input() startDate: string = '';
    @Input() endDate: string = '';
    @Output() rangeChange = new EventEmitter<{ start: string, end: string }>();

    error: string | null = null;

    constructor() { }

    ngOnInit(): void {
    }

    onDateChange() {
        this.error = null;
        if (this.startDate && this.endDate) {
            if (new Date(this.startDate) > new Date(this.endDate)) {
                this.error = 'Start date cannot be after End date';
                // Do not emit invalid range
                return;
            }
        }
        this.rangeChange.emit({ start: this.startDate, end: this.endDate });
    }
}

