import { Component, EventEmitter, Input, OnInit, Output } from '@angular/core';

@Component({
    selector: 'app-form-wrapper',
    templateUrl: './app-form-wrapper.component.html',
    styleUrls: ['./app-form-wrapper.component.scss']
})
export class AppFormWrapperComponent implements OnInit {

    @Input() title: string = 'Form';
    @Input() loading: boolean = false;
    @Input() submitLabel: string = 'Save';
    @Input() cancelLabel: string = 'Cancel';
    @Input() isInvalid: boolean = false;

    @Output() submitForm = new EventEmitter<void>();
    @Output() cancelForm = new EventEmitter<void>();

    constructor() { }

    ngOnInit(): void {
    }

    onSubmit() {
        if (!this.loading && !this.isInvalid) {
            this.submitForm.emit();
        }
    }

    onCancel() {
        this.cancelForm.emit();
    }
}

